import React from "react";
import {
  Text,
  View,
  ImageBackground,
  StyleSheet,
  SafeAreaView,
  ScrollView,
  Image,
  Dimensions,
  Alert,
  TextInput,
} from "react-native";

import InputField from "../components/InputField";
import NavBar from "../components/NavBar";
import WhButton from "../components/WhButton";

function WaitList() {
  return (
    <SafeAreaView style={styles.container}>
      <NavBar />
      <ImageBackground
        style={styles.background}
        source={require("../assets/landing-page-background.jpg")}
      >
        <View>
          <Image
            source={require("../assets/wh-logo.png")}
            style={styles.whLogo}
          />

          <Text style={styles.text}>Join Waitlist</Text>
          <View style={styles.input}>
            <TextInput
              email=" Email"
              placeholder="Email"
              autoCapitalize="none"
              keyboardType="email-address"
            />
            <WhButton title="Join" />
          </View>
        </View>
      </ImageBackground>
    </SafeAreaView>
  );
}

const styles = StyleSheet.create({
  container: {
    backgroundColor: "#000",
    resizeMode: "cover",
    flex: 1,
    justifyContent: "center",
  },
  background: {
    flex: 1,
    width: "100%",
    height: 850,
    alignItems: "center",
    justifyContent: "center",
    paddingTop: "8%",
  },
  input: {
    justifyContent: "center",
    width: "75%",
    height: 40,
    backgroundColor: "#f2f3f5",
    borderRadius: 10,
    borderColor: "rgba(37,37,37,255)",
    fontSize: 20,
    // marginTop: 12,
    paddingLeft: 8,
    shadowColor: "black",
    shadowOffset: { width: 5, height: 5 },
    shadowOpacity: 0.9,
    shadowRadius: 10,
  },
  text: {
    backgroundColor: "black",
    flex: 1,
    color: "white",
    fontFamily: "HelveticaRegular",
    fontSize: 30,
    opacity: 0.9,
    marginTop: "30%",
  },
  whLogo: {
    // flex: 1,
    width: "80%",
    height: 130,
    resizeMode: "contain",
    // position: "absolute",
    marginTop: "8%",
    // marginLeft: "10%",
  },
});

export default WaitList;
