import React from "react";
import { View, Image, StyleSheet, Text, FlatList } from "react-native";
import movies from "../components/movies.json";

const clips = movies[0];

function Carousel({ poster, title }) {
  return (
    <View style={styles.container}>
      <View style={styles.carousel}>
        {/* <Image source={require("../assets/clip.png")}  /> */}
        <FlatList
          data={clips}
          // keyExtractor={(movie) => movie.id.toString()}
          horizontal
          pagingEnabled
          renderItem={({ item }) => <Image style={styles.clip} {...poster} />}
        />
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    // flex: 1,
    width: "100%",
    // justifyContent: "center",
    // resizeMode: "contain",
    flexDirection: "row",
  },
  carousel: {
    width: "80%",
    // justifyContent: "center",
    // resizeMode: "contain",
  },

  clip: {
    // marginTop: "2%",
    resizeMode: "contain",
    width: "40%",
    height: 100,
    // padding: 20,
    margin: 5,
  },
  clipText: {
    color: "white",
    fontSize: 16,
  },
});

export default Carousel;
