import React from 'react';
import {
    StyleSheet,
    Platform,
    View,
    Text,
    StatusBar,
    ImageBackground,
    TouchableHighlight,
    Image
} from "react-native";

import MenuText from "../components/MenuText";


function GladiatorsLandingPage() {
    return (


        <View style={styles.container}>
            <ImageBackground
                resizeMode="contain"
                style={styles.background}
                source={require("../assets/Background.jpg")}>
                <Image style={styles.logo}
                    source={require("../assets/wheelhouseCornerLogo.png")}>
                </Image>
                <View style={styles.menuBar}>
                    <View style={styles.menu}>
                        <MenuText>WATCH TRAILER</MenuText>
                        <MenuText>GLADIATORS OF STEEL</MenuText>
                        <MenuText>CLIPS</MenuText>
                    </View>
                </View>
            </ImageBackground>
        </View>
    )
}

const styles = StyleSheet.create({
    container: {
        backgroundColor: "#000",
        flex: 1,
        flexDirection: "row",
        paddingTop: Platform.OS === "android" ? StatusBar.currentHeight : 0,
        position: "relative",
    },
    background: {
        // flexDirection: "row",
        // justifyContent: "flex-start",

        width: "100%",
        height: "100%"
    },
    logo: {
        height: "40%",
        resizeMode: "contain",
        // justifyContent: "flex-start"
    },
    menu: {
        flexDirection: "row",
        justifyContent: "space-evenly"
    },
    menuBar: {
        flex: 2,
        justifyContent: "flex-end"
    },

    text: {
        fontSize: 20,
        fontWeight: "bold",
        color: "white",
        paddingTop: 150,
        paddingLeft: 190,
        opacity: .8,
        // textShadowColor: "black",
        // textShadowOffset: { width: 10, height: -10 },
        // textShadowRadius: 7,
        fontFamily: Platform.OS === "android" ? "Roboto" : "Avenir"
    }

});

export default GladiatorsLandingPage
