import React from 'react';
import {
    StyleSheet,
    Platform,
    View,
    Text,
    SafeAreaView,
    StatusBar,
    ImageBackground,
    Image,
    TouchableOpacity

} from "react-native";
import { NavigationContainer } from "@react-navigation/native";
import { createNativeStackNavigator } from "@react-navigation/native-stack";

import MenuText from "../components/MenuText";


function HomePage() {
    return (


        // <SafeAreaView style={styles.container}>
        <ImageBackground
            resizeMode="cover"
            style={styles.background}
            source={require("../assets/landing-page-background.jpg")}>
            <View style={styles.header}>
            </View>
            <Image
                source={require("../assets/wh-logo.png")}
                style={styles.whLogo}>
            </Image>
            <Image
                resizeMode={'contain'}
                source={require("../assets/cars-wh-landing.png")}
                style={styles.carsImage}>
            </Image>
            <Text style={styles.text}>Font Please</Text>
            <View style={styles.menuBar}>
                <View style={styles.menu}>
                    <TouchableOpacity title="Watch Trailer" onPress={() => { console.log("Button Pressed") }}>
                        <MenuText>WATCH TRAILER</MenuText>
                    </TouchableOpacity>
                    <TouchableOpacity>
                        <MenuText>GLADIATORS OF STEEL</MenuText>
                    </TouchableOpacity>
                    <TouchableOpacity>
                        <MenuText>CLIPS</MenuText>
                    </TouchableOpacity>
                </View>
            </View>
        </ImageBackground>
        // </SafeAreaView>
    )
}

const styles = StyleSheet.create({
    container: {
        backgroundColor: "#000",
        flex: 1,
        resizeMode: "contain",
        justifyContent: "center",
        paddingTop: Platform.OS === "android" ? StatusBar.currentHeight : 0
    },
    background: {
        flex: 1,
        width: "100%",
        height: "auto"
    },
    carsImage: {
        flex: 1,
        width: "100%",
        // marginTop: "15%",
        // height: 200
        // resizeMode: "contain"
    },
    header: {
        // flex: 1,
        width: "100%",
        height: 20,
        backgroundColor: "grey"
    },
    menu: {
        flexDirection: "row",
        justifyContent: "space-evenly"
    },
    menuBar: {
        flex: 2,
        justifyContent: "flex-end"
    },

    text: {
        fontSize: 20,
        fontWeight: "bold",
        color: "white",
        paddingTop: 150,
        paddingLeft: 190,
        opacity: .8,
        // fontFamily: Platform.OS === "android" ? "Roboto" : "Avenir"
        // fontFamily: "HelveticaRegular"
    },

    whLogo: {
        flex: 2,
        width: "90%",
        // height: "auto",
    }

});

export default HomePage
