import React from 'react';
import { View, StyleSheet, Image } from 'react-native';
import MenuText from './MenuText';

function Card({ title, image }) {
    return (
        <View style={styles.card}>
            <Image source={image} />
            <MenuText>{title}</MenuText>
        </View>
    );
}

const styles = StyleSheet.create({
    card: {
        borderColor: "#fff",
        borderStyle: "solid",
        borderWidth: 5
    },
})

export default Card;
