import React from 'react';
import {
    StyleSheet,
    Platform,
    View,
    Text,
    StatusBar,
    ImageBackground,
    TouchableHighlight,
    Image
} from "react-native";

import MenuText from "../components/MenuText";
import Card from "../components/Card";


function GladiatorsLandingPage() {
    return (


        <View style={styles.container}>
            <ImageBackground
                style={styles.background}
                source={require("../assets/GladiatorsBackground.jpg")}>
                <View style={styles.card}>
                    <Card
                        title="PLAY TRAILER"
                        image={require("../assets/trailerThumb.jpg")}
                    />
                    <Card
                        title="PLAY CLIP 1"
                        image={require("../assets/playClip1.jpg")}
                    />
                    <Card
                        title="WATCH FILM"
                        image={require("../assets/watchFilm.jpg")}
                    />
                </View>

                <View style={styles.menuBar}>
                    <View style={styles.menu}>
                        <MenuText>UPCOMING SHOWS</MenuText>
                        <MenuText>WAITLIST</MenuText>
                        <MenuText>GLADIATORS OF STEEL</MenuText>
                        <MenuText>STORE</MenuText>
                    </View>
                </View>
            </ImageBackground>
        </View>
    )
}

const styles = StyleSheet.create({
    container: {
        backgroundColor: "#000",
        flex: 1,
        paddingTop: Platform.OS === "android" ? StatusBar.currentHeight : 0
    },
    background: {
        flex: 1,
        resizeMode: "contain",
        width: "100%",
        height: "100%"
    },
    card: {
        flexDirection: "row",
        justifyContent: "center",
        alignItems: "center"
    },
    menu: {
        flexDirection: "row",
        justifyContent: "space-evenly"
    },
    menuBar: {
        flex: 2,
        justifyContent: "flex-end"
    },

    text: {
        fontSize: 20,
        fontWeight: "bold",
        color: "white",
        paddingTop: 150,
        paddingLeft: 190,
        opacity: .8,
        // textShadowColor: "black",
        // textShadowOffset: { width: 10, height: -10 },
        // textShadowRadius: 7,
        fontFamily: Platform.OS === "android" ? "Roboto" : "Avenir"
    }

});

export default GladiatorsLandingPage
