import React from "react";
import { View, StyleSheet, Image, TouchableOpacity, Text } from "react-native";
import MenuText from "./MenuText";

function Card(props) {
  console.log(props);
  return (
    // <TouchableOpacity>
    //
    // </TouchableOpacity>
    <View style={styles.container}>
      {/* {props.children} */}
      {/* <ScrollView horizontal={true}> */}
      <Image style={styles.image} {...props.poster} />
      <Text style={styles.text}>{props.title}</Text>
      {/* </ScrollView> */}
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flexDirection: "row",
    width: "100%",
    height: 170,
    // justifyContent: "center",
    backgroundColor: "white",
  },
  image: {
    resizeMode: "contain",
    width: "100%",
    height: 170,
  },
  text: {
    textAlign: "center",
    color: "red",
    opacity: 1,
    fontSize: 15,
    fontWeight: "bold",
    paddingTop: 20,
  },
});

export default Card;
