import React, {useState} from "react";
import { StyleSheet, Text, View, SafeAreaView, StatusBar } from 'react-native';
import { NavigationContainer, StackActions } from '@react-navigation/native';
import homePage from "./app/screens/homePage";
import gladiatorsLandingPage from "./app/screens/gladiatorsLandingPage";
import { useDimensions, useDeviceOrientation } from "@react-native-community/hooks";
import Navigator from "./app/routes/drawer";
import * as Font from "expo-font";
import AppLoading from 'expo-app-loading';


const getFonts= () => Font.loadAsync({  
  "HelveticaRegular" : require("./app/assets/fonts/Yantramanav-Bold.ttf"),
  "Helvetica87Heavy" : require("./app/assets/fonts/Montserrat-VariableFont_wght.ttf")
});

export default function App() {

  // console.log(useDeviceOrientation());

  const [fontsLoaded, setFontsLoaded] = useState(false);
  if (fontsLoaded){
    return (
      <Navigator />
    );
  } else {
    return(
      <AppLoading 
    startAsync={getFonts}
    onFinish={ () => setFontsLoaded(true)}
    onError={console.warn}
    />
    )
  }

}
