import React from "react";
import {
  View,
  Image,
  StyleSheet,
  Text,
  FlatList,
  ScrollView,
} from "react-native";
import Card from "./Card";
import movies from "../components/movies.json";

function Carousel() {
  return (
    <View>
      {movies.map((movie) => {
        return (
          <View key={movie.id} style={styles.container}>
            <Card key={movie.id} poster={movie.poster} title={movie.title} />
          </View>
        );
      })}
    </View>
  );
}

