import React, { useState } from "react";
import {
  StyleSheet,
  Platform,
  Text,
  SafeAreaView,
  StatusBar,
  ImageBackground,
  Image,
  TouchableOpacity,
  View,
  Button,
  Alert,
} from "react-native";

import { Auth } from "aws-amplify";

import InputField from "../components/InputField";
import WhButton from "../components/WhButton";

import { NavigationContainer } from "@react-navigation/native";
import { useNavigation } from "@react-navigation/native";
import { useForm, Controller } from "react-hook-form";

const EMAIL_REGEX =
  /^[a-zA-Z0-9.!#$%&'*+/=?^_`{|}~-]+@[a-zA-Z0-9-]+(?:\.[a-zA-Z0-9]+)*$/;

function SignIn({ props }) {
  // const [email, setEmail] = useState("");
  // const [password, setPassword] = useState("");
  const navigation = useNavigation();
  const [loading, setLoading] = useState(false);

  const {
    control,
    handleSubmit,
    formState: { errors },
  } = useForm();

  console.log(errors);

  const onLoginPressed = async (data) => {
    if (loading) {
      return;
    }
    setLoading(true);
    try {
      const response = await Auth.signIn(
        data.username,
        data.password || data.email,
        data.password
      );
      Alert.alert("Success!");
      console.log(response);
      navigation.navigate("Home");
    } catch (e) {
      Alert.alert("Oopsie, ", e.message);
    }
    setLoading(false);
  };

  const onForgotPasswordPressed = () => {
    navigation.navigate("ForgotPassword");
  };
  const onSignUpPressed = () => {
    navigation.navigate("SignUp");
  };
  return (
    <SafeAreaView style={styles.container}>
      <ImageBackground
        style={styles.background}
        source={require("../assets/landing-page-background.jpg")}
      >
        <Image
          source={require("../assets/wh-logo.png")}
          style={styles.whLogo}
        />
        <Text style={styles.loginText}>Login</Text>
        <View style={styles.inputs}>
          <InputField
            name="username"
            placeholder="Username"
            control={control}
            rules={{
              required: "Username is required",
            }}
          />
          <InputField
            name="password"
            placeholder="Password"
            control={control}
            rules={{
              required: "Password is required",
              minLength: {
                value: 8,
                message: "Password minimum length is 8 characters",
              },
            }}
            secureTextEntry
          />
          <Text style={styles.or}>Or</Text>
          <InputField
            name="email"
            placeholder="Email"
            control={control}
            rules={{
              required: "Email is required",
              pattern: { value: EMAIL_REGEX, message: "Must be a valid Email" },
            }}
            autoCapitalize="none"
            keyboardType="email-address"
          />

          <InputField
            name="password"
            placeholder="Password"
            control={control}
            rules={{
              required: "Password is required",
              minLength: {
                value: 8,
                message: "Password minimum length is 8 characters",
              },
            }}
            secureTextEntry
          />
          <WhButton
            title={loading ? "loading..." : "Login"}
            onPress={handleSubmit(onLoginPressed)}
          />
          {/* <View style={styles.signUpContainer}> */}

          <Text style={styles.signupText}>Forgot Password?</Text>
          <Button title="Click Here" onPress={onForgotPasswordPressed} />
          <View>
            <Text style={styles.signupText}>Haven't signed up?</Text>
            <Button
              style={styles.signupText}
              title="Sign up here"
              onPress={onSignUpPressed}
            />
          </View>
        </View>
      </ImageBackground>
    </SafeAreaView>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#000",
    paddingTop: Platform.OS === "android" ? StatusBar.currentHeight : 0,
  },
  background: {
    flex: 1,
    width: "100%",
    height: "auto",
    alignItems: "center",
  },
  inputs: {
    width: "100%",
    height: "50%",
    alignItems: "center",
    marginTop: "5%",
  },
  loginText: {
    color: "white",
    fontSize: 30,
    fontFamily: "HelveticaRegular",
    opacity: 0.9,
    paddingTop: "5%",
    // marginTop: "18%",
  },
  or: {
    color: "white",
    fontSize: 20,
    fontFamily: "HelveticaRegular",
    opacity: 0.9,
    paddingTop: "5%",
  },
  signupText: {
    color: "white",
    fontSize: 18,
    fontFamily: "HelveticaRegular",
    marginTop: "8%",
    opacity: 0.9,

    // justifyContent: "center",
    // alignItems: "center",
  },
  whLogo: {
    width: "80%",
    height: 130,
    resizeMode: "contain",
    // position: "absolute",
    marginTop: "8%",
  },
});

export default SignIn;
