import React from 'react';
import { View, StyleSheet, Image, TouchableOpacity, Text } from 'react-native';
import MenuText from './MenuText';

function Card({ image }) {
    return (

        <TouchableOpacity>
            <View style={styles.card}>
                <Image style={styles.image} source={image} />
            </View>
        </TouchableOpacity>

    );
}

const styles = StyleSheet.create({
    card: {
        borderColor: "#fff",
        borderStyle: "solid",
        borderWidth: 3,
        width: "100%",
        height: 80
    },
    image: {
        width: "100%",
        height: 75
    },
    text: {
        justifyContent: "center",
        color: "white",
        opacity: 1,
        fontSize: 18,
        fontWeight: "bold",
        color: "white"
    },
})

export default Card;
