import React from "react";
import {
  View,
  Image,
  StyleSheet,
  Text,
  FlatList,
  ScrollView,
} from "react-native";
import movies from "../components/movies.json";
// import Card from "./Card";

const clips = movies[0];

function Carousel() {
  return (
    <View style={styles.carousel}>
      <ScrollView horizontal={true}>
        <Image style={styles.clip} source={movies[0].poster} />
        <Text> {movies[0].title} </Text>
        <Image />
        <Text></Text>
        <Image />
        <Text></Text>
      </ScrollView>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    // flex: 1,
    width: "100%",
    // justifyContent: "center",
    // resizeMode: "contain",
    flexDirection: "row",
  },
  carousel: {
    width: "80%",
    flexDirection: "row",
    // justifyContent: "center",
    // resizeMode: "contain",
  },

  clip: {
    // marginTop: "2%",
    resizeMode: "contain",
    width: "40%",
    height: 175,
    // padding: 20,
    // margin: 5,
  },
  clipText: {
    color: "white",
    fontSize: 16,
  },
});

export default Carousel;
