import React from "react";
import {
  View,
  Image,
  StyleSheet,
  Text,
  FlatList,
  ScrollView,
} from "react-native";

function Carousel(props) {
  return (
    <View style={styles.container}>
      <View style={styles.carousel}>
        <ScrollView horizontal={true}>
          <Image style={styles.clip} source={props.poster} />
          <Text style={styles.clipText}>{props.title}</Text>
        </ScrollView>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    // flex: 1,
    width: "100%",
    // justifyContent: "center",
    // resizeMode: "contain",
    flexDirection: "row",
  },
  carousel: {
    width: "80%",

    // justifyContent: "center",
    // resizeMode: "contain",
  },

  clip: {
    // marginTop: "2%",
    resizeMode: "contain",
    width: "20%",
    height: 175,
    flexDirection: "row",
    // padding: 20,
    // margin: 5,
  },
  clipText: {
    color: "white",
    fontSize: 16,
  },
});

export default Carousel;
