import React from "react";
import { View, StyleSheet, Image, TouchableOpacity, Text } from "react-native";
import MenuText from "./MenuText";

function Card({ poster, title }) {
  return (
    <TouchableOpacity>
      <View style={styles.container}>
        <Image style={styles.image} source={poster} />
        <Text style={styles.text}> {title} </Text>
      </View>
    </TouchableOpacity>
  );
}

const styles = StyleSheet.create({
  container: {
    flexDirection: "row",
    width: "100%",
    height: 170,
    // justifyContent: "center",
    backgroundColor: "white",
  },
  image: {
    borderColor: "black",
    borderWidth: 3,
    resizeMode: "contain",
    width: "40%",
    height: 170,
    // marginLeft: "19%",
    // marginRight: "20%"
  },
  text: {
    textAlign: "center",
    color: "red",
    opacity: 1,
    fontSize: 15,
    fontWeight: "bold",
    paddingTop: 20,
  },
});

export default Card;
