import React from "react";
import { View, Image, StyleSheet, FlatList } from "react-native";
import movies from "../assets/data/videos";

const clips = movies[0];

function Carousel(props) {
  return (
    // <View style={styles.container}>
    <View style={styles.carousel}>
      <Image
        style={styles.redline}
        source={require("../assets/redline-horizontal.png")}
      />
      <Image source={require("../assets/clip.png")} style={styles.clip} />
      <FlatList
        data={clips}
        horizontal
        pagingEnabled
        renderItem={({ item }) => {
          return (
            <View>
              <Image source={{ uri: item }} />
            </View>
          );
        }}
      />

      <Image
        style={styles.redline}
        source={require("../assets/redline-horizontal.png")}
      />
    </View>
    // </View>
  );
}

const styles = StyleSheet.create({
  container: {
    // flex: 1,
    width: "100%",
    // justifyContent: "center",
    // resizeMode: "contain",
    flexDirection: "row",
  },
  carousel: {
    width: "80%",
    // justifyContent: "center",
    // resizeMode: "contain",
  },

  clip: {
    // marginTop: "2%",
    resizeMode: "contain",
    width: "40%",
    height: 175,
    // padding: 20,
  },

  redline: {
    // flex: 1,
    width: "100%",
    height: 45,
    resizeMode: "contain",
  },
});

export default Carousel;
